import React, { useState, useEffect } from "react";
import queryString from "query-string";
import { Link } from "react-router-dom";
import io from "socket.io-client";
import OnlinePlayersAutonomous from "../OnlinePlayersAutonomous/OnlinePlayersAutonomous";
import OnlinePlayersHoC from "../OnlinePlayersHoC/OnlinePlayersHoC";
import SimpleOnlinePlayerList from "../OnlinePlayersHoC/SimpleOnlinePlayerList";
import OnlinePlayersRenderProps from "../OnlinePlayersRenderProps/OnlinePlayersRenderProps";
import OnlinePlayersHooks from "../OnlinePlayersHooks/OnlinePlayersHooks";
const Lobby = ({ location }) => {
  const { nameFromURL } = queryString.parse(location.search);
  const ENDPOINT = "http://localhost:3002";
  console.log("Lobby Name from URL: " + { nameFromURL });

  useEffect(() => {
    var socket = io(ENDPOINT);

    console.log("join");

    socket.emit("join", { nameFromURL }, (error) => {
      if (error) {
        alert(error);
      }
    });
  }, [ENDPOINT, { nameFromURL }]);

  return (
    <div>
      <h2>HALLO {nameFromURL}</h2>
      lobby
      <ul>
        <Link to={`/game?name=${nameFromURL}&room=$1`}>
          <li>Room 1</li>
        </Link>
        <Link to={`/game?name=${nameFromURL}&room=$2`}>
          <li>Room 2</li>
        </Link>
        <Link to={`/game?name=${nameFromURL}&room=$3`}>
          <li>Room 3</li>
        </Link>
      </ul>
      <OnlinePlayersHooks nameData={nameFromURL}></OnlinePlayersHooks>
    </div>
  );
};

export default Lobby;
